function checkName(){var e=document.getElementById("name").value;""==e?document.getElementById("nameSpan").innerHTML="姓名不能为空":e.length<3?document.getElementById("nameSpan").innerHTML="姓名不能少于4位":e.length<10?document.getElementById("nameSpan").innerHTML="√":document.getElementById("nameSpan").innerHTML="名字太长"}function checkPhone(){var e=document.getElementById("phoneNum").value,n=/^1\d{10}$/;n.test(e)?document.getElementById("phoneNumSpan").innerHTML="√":""==e?document.getElementById("phoneNumSpan").innerHTML="手机号不能为空":document.getElementById("phoneNumSpan").innerHTML="×"}function checkEmail(){var e=document.getElementById("email").value,n=/^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+(.[a-zA-Z0-9_-])+/;n.test(e)?document.getElementById("emailSpan").innerHTML="√":""==e?document.getElementById("emailSpan").innerHTML="邮箱不能为空":document.getElementById("emailSpan").innerHTML="×"}function checkPwd(){var e=document.getElementById("pwd").value;""==e?document.getElementById("pwdSpan").innerHTML="密码不能为空":e.length<6?document.getElementById("pwdSpan").innerHTML="×":isNaN(e)?document.getElementById("pwdSpan").innerHTML="√":document.getElementById("pwdSpan").innerHTML="密码不能为纯数字"}function checkPwd2(){var e=document.getElementById("pwd").value,n=document.getElementById("pwd2").value;e!=n?document.getElementById("pwd2Span").innerHTML="×":""==n?document.getElementById("pwd2Span").innerHTML="密码不能为空":document.getElementById("pwd2Span").innerHTML="√"}